class BilibiliFavoritesData {
  BilibiliFavoritesData({
      this.code, 
      this.message, 
      this.ttl, 
      this.data,});

  BilibiliFavoritesData.fromJson(dynamic json) {
    code = json['code'];
    message = json['message'];
    ttl = json['ttl'];
    data = json['data'] != null ? Data.fromJson(json['data']) : null;
  }
  int? code;
  String? message;
  int? ttl;
  Data? data;

  Map<String, dynamic> toJson() {
    final map = <String, dynamic>{};
    map['code'] = code;
    map['message'] = message;
    map['ttl'] = ttl;
    if (data != null) {
      map['data'] = data?.toJson();
    }
    return map;
  }

}

